'use strict';let id="";$(".btn-danger").click(function(a){id=$(this).attr("id");axios({method:"POST",url:`/bookmark/${id}?_method=DELETE`,responseType:"stream"}).then(b=>{swal({title:"Deleted Bookmark",text:"Kudos! You've successfully deleted the bookmark. ",icon:"success",timer:5500,closeOnClickOutside:!1});setInterval(()=>{window.location.reload()},3E3)}).catch(b=>console.log(b))});
$(".upd_btn").click(function(a){a.preventDefault();id=$(this).attr("id");a=$(this).attr("rel");let b=$(this).attr("href"),c=$(this).data("url");$(".edit_form").fadeIn("slow");$("#edit_name").attr("value",a);$("#edit_url").attr("value",c);$("#edit_description").html(b);$("#edit_info").html(`Editing Bookmark- ${a}`)});
$("#edit_form").submit(function(a){a.preventDefault();a=$(this).serialize();axios({method:"Post",url:`/bookmark/${id}?_method=PUT&${a}`,data:a}).then(b=>{swal({title:"Updated Bookmark",text:"Kudos! You've successfully updated the bookmark. ",icon:"success",timer:5500,closeOnClickOutside:!1});setInterval(()=>{window.location.reload()},3E3)}).catch(b=>console.log(b))});